import React ,{Component} from "react";

class Ltem1 extends Component{
render(){
  const {araay}=this.props;
  const theItem= araay.map((item)=>{
    return(
      <div>
        <p> {araay.name} </p>
        <img src={araay.img}/>
        <p> {araay.twater}</p>
      </div>
    )
  })
  return(
    <div>
     {theItem}
    </div>
  )
}  



}

export default Ltem1;
